// If no segments in current file, show file list instead
if (filteredBlocks.length === 0 && blocks.length > 0) {
    // Group blocks by file and count segments
    const fileMap = new Map<string, { count: number; blocks: HighlightSegment[] }>();

    blocks.forEach(block => {
        if (!fileMap.has(block.filePath)) {
            fileMap.set(block.filePath, { count: 0, blocks: [] });
        }
        const entry = fileMap.get(block.filePath)!;
        entry.count++;
        entry.blocks.push(block);
    });

    // Transform to file list format
    const fileList = Array.from(fileMap.entries()).map(([filePath, data]) => ({
        filePath,
        fileName: this._getFileName(filePath),
        segmentCount: data.count,
        // Get preview of first segment name
        previewName: this._getSegmentName(data.blocks[0])
    }));

    Logger.debug(`[SegmentsWebviewProvider] No segments in current file, showing ${fileList.length} files with segments`);

    this._view.webview.postMessage({
        command: 'showFileList',
        files: fileList
    });
    return;
}

function renderFileList(files) {
    const container = document.getElementById('segments-list');
    const countEl = document.getElementById('segment-count');

    if (!files || files.length === 0) {
        container.innerHTML = `
            <div class="empty-state">
                <div class="codicon codicon-inbox"></div>
                <p>No segments detected yet.</p>
                <p style="margin-top: 8px; font-size: 12px;">Segments will appear here as you work.</p>
            </div>
        `;
        countEl.textContent = 'No segments';
        return;
    }

    countEl.textContent = `${files.length} file${files.length !== 1 ? 's' : ''} with segments`;

    // Hide sort controls when showing file list
    const sortControls = document.querySelector('.sort-controls');
    if (sortControls) {
        sortControls.style.display = 'none';
    }

    container.innerHTML = `
        <div class="file-list-header">
            <div class="codicon codicon-info"></div>
            <p>No segments in current file. Click a file below to view its segments:</p>
        </div>
        ${files.map(file => `
            <div class="file-list-item" data-file-path="${escapeHtml(file.filePath)}">
                <div class="file-list-item-icon">
                    <span class="codicon codicon-file-code"></span>
                </div>
                <div class="file-list-item-content">
                    <div class="file-list-item-title">${escapeHtml(file.fileName)}</div>
                    <div class="file-list-item-subtitle">${file.segmentCount} segment${file.segmentCount !== 1 ? 's' : ''}</div>
                </div>
                <div class="file-list-item-action">
                    <span class="codicon codicon-chevron-right"></span>
                </div>
            </div>
        `).join('')}
    `;

    // Add click handlers for file items
    container.querySelectorAll('.file-list-item').forEach(item => {
        item.addEventListener('click', () => {
            const filePath = item.dataset.filePath;
            vscode.postMessage({
                command: 'openFile',
                filePath
            });
        });
    });
}

