const dismissBtn = target.closest('.dismiss-btn');
if (dismissBtn) {
    const card = dismissBtn.closest('[data-segment-id]');
    if (card) {
        e.preventDefault();
        const segmentId = card.dataset.segmentId;
        dismissSegment(segmentId);
    }
    return;
}

