// Find the current segment index
const currentIndex = segments.findIndex(s => s.id === segmentId);

// Find the card element
const card = document.querySelector(`.segment-card[data-segment-id="${segmentId}"]`);
if (!card) return;

// Add dismissing class to trigger animation
card.classList.add('dismissing');

// Wait for animation to complete before removing from DOM and notifying backend
setTimeout(() => {
    // Remove from DOM
    card.remove();

    // Remove from local state
    segments = segments.filter(s => s.id !== segmentId);

    // Update segment count
    const countEl = document.getElementById('segment-count');
    if (countEl) {
        countEl.textContent = segments.length === 0
            ? 'No segments'
            : `${segments.length} segment${segments.length !== 1 ? 's' : ''}`;
    }

    // Notify backend
    vscode.postMessage({
        command: 'dismissSegment',
        segmentId
    });

    // Auto-advance: Navigate to next segment
    if (segments.length > 0) {
        // Get the next segment (or first if we dismissed the last one)
        const nextIndex = currentIndex >= segments.length ? 0 : currentIndex;
        const nextSegment = segments[nextIndex];

        if (nextSegment) {
            // Tell backend to navigate to this segment
            vscode.postMessage({
                command: 'goToSegment',
                segmentId: nextSegment.id,
                startLine: nextSegment.startLine,
                endLine: nextSegment.endLine,
                filePath: nextSegment.filePath
            });
        }
    }

case 'goToSegment':
    if (message.filePath && message.startLine !== undefined && message.endLine !== undefined) {
        Logger.debug(`[SegmentsWebviewProvider] Navigating to segment: ${message.filePath}:${message.startLine}-${message.endLine}`);
        const uri = vscode.Uri.file(message.filePath);
        const document = await vscode.workspace.openTextDocument(uri);
        const editor = await vscode.window.showTextDocument(document);

        // Create range for the segment (convert from 1-based to 0-based)
        const startLine = message.startLine - 1;
        const endLine = message.endLine - 1;
        const range = new vscode.Range(startLine, 0, endLine, 999);

        // Set selection and reveal with InCenter for perfect positioning
        editor.selection = new vscode.Selection(range.start, range.end);
        editor.revealRange(range, vscode.TextEditorRevealType.InCenter);
    }
    break;
