 * Force remove a block, even if it's starred or has context notes
 * Includes confirmation for important segments
 */
public async forceRemove(blockId: string, skipConfirmation: boolean = false): Promise<boolean> {
    const segment = this.highlighter.getSegment(blockId);
    if (!segment) {
        Logger.warn(`Cannot force remove: segment ${blockId} not found`);
        return false;
    }

    // Check if segment has important data
    const isStarred = segment.metadata?.starred === true;
    const hasContextNote = segment.metadata?.contextNote && segment.metadata.contextNote.trim().length > 0;
    const isImportant = isStarred || hasContextNote;

    // Show confirmation for important segments unless skipped
    if (isImportant && !skipConfirmation) {
        const details: string[] = [];
        if (isStarred) {
            details.push('starred');
        }
        if (hasContextNote) {
            details.push('has context notes');
        }

        const result = await vscode.window.showWarningMessage(
            `This segment is ${details.join(' and ')}. Are you sure you want to permanently remove it?`,
            { modal: true },
            'Remove',
            'Cancel'
        );

        if (result !== 'Remove') {
            return false;
        }
    }

    // Perform removal
    this.remove(blockId);
    Logger.info(`Force removed segment ${blockId} (important: ${isImportant})`);
    return true;
}

/**
 * Force remove all stale segments
 * Useful for cleaning up outdated segments
 */
public async forceRemoveAllStale(): Promise<number> {
    const staleSegments = this.highlighter.getSegmentsByState(SegmentState.STALE);

    if (staleSegments.length === 0) {
        vscode.window.showInformationMessage('No stale segments to remove');
        return 0;
    }

    // Count important segments
    const importantCount = staleSegments.filter(s =>
        s.metadata?.starred || (s.metadata?.contextNote && s.metadata.contextNote.trim().length > 0)
    ).length;

    // Show confirmation
    const message = importantCount > 0
        ? `Remove ${staleSegments.length} stale segments? (${importantCount} have stars or context notes)`
        : `Remove ${staleSegments.length} stale segments?`;

    const result = await vscode.window.showWarningMessage(
        message,
        { modal: true },
        'Remove All',
        'Cancel'
    );

    if (result !== 'Remove All') {
        return 0;
    }

    // Remove all stale segments
    let removedCount = 0;
    for (const segment of staleSegments) {
        await this.forceRemove(segment.id, true); // Skip individual confirmations
        removedCount++;
    }

    vscode.window.showInformationMessage(`Removed ${removedCount} stale segments`);
    return removedCount;
}

