/* Subtle highlight for expanded/selected segments based on complexity */
.segment-card.expanded[data-complexity="low"] {
    background: linear-gradient(
        to right,
        rgba(52, 199, 89, 0.04),
        transparent 50%
    );
}

.segment-card.expanded[data-complexity="medium"] {
    background: linear-gradient(
        to right,
        rgba(255, 159, 10, 0.04),
        transparent 50%
    );
}

.segment-card.expanded[data-complexity="high"] {
    background: linear-gradient(
        to right,
        rgba(255, 69, 58, 0.04),
        transparent 50%
    );
}

.segment-card.expanded[data-complexity="unknown"] {
    background: linear-gradient(
        to right,
        rgba(142, 142, 147, 0.03),
        transparent 50%
    );
}
